INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_const_1_1', 'u_const_1', '{"en": "Personal Protective Equipment (PPE)", "zh": "個人防護裝備 (PPE)"}', 'IMAGE_QUIZ', '[{"question_audio": "audio/osha_helmet.mp3", "prompt": {"en": "Select: ''Use the helmet''", "zh": "請選擇：''戴上安全帽'' (Use el casco)"}, "options": [{"id": "opt1", "text": {"en": "Helmet (Casco)", "zh": "安全帽 (Casco)"}, "image": "assets/images/helmet.png", "correct": true}, {"id": "opt2", "text": {"en": "Gloves (Guantes)", "zh": "手套 (Guantes)"}, "image": "assets/images/gloves.png", "correct": false}, {"id": "opt3", "text": {"en": "Boots (Botas)", "zh": "靴子 (Botas)"}, "image": "assets/images/boots.png", "correct": false}, {"id": "opt4", "text": {"en": "Vest (Chaleco)", "zh": "背心 (Chaleco)"}, "image": "assets/images/vest.png", "correct": false}]}, {"question_audio": "audio/osha_harness.mp3", "prompt": {"en": "Select: ''Tie off the harness''", "zh": "請選擇：''繫好安全帶'' (Amárrese el arnés)"}, "options": [{"id": "opt1", "text": {"en": "Harness (Arnés)", "zh": "安全帶 (Arnés)"}, "image": "assets/images/harness.png", "correct": true}, {"id": "opt2", "text": {"en": "Ladder (Escalera)", "zh": "梯子 (Escalera)"}, "image": "assets/images/ladder.png", "correct": false}]}]', 'kc_safety_imperatives', 1);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_const_1_2', 'u_const_1', '{"en": "Fall Protection & Ladders", "zh": "墜落防護與梯子"}', 'IMAGE_QUIZ', '[{"question_audio": "audio/osha_ladder_angle.mp3", "prompt": {"en": "Select: ''Keep ladder at correct angle''", "zh": "請選擇：''保持梯子正確角度''"}, "options": [{"id": "opt1", "text": {"en": "Correct Angle", "zh": "正確角度"}, "image": "assets/images/ladder_correct.png", "correct": true}, {"id": "opt2", "text": {"en": "Flat on ground", "zh": "平放地面"}, "image": "assets/images/ladder_flat.png", "correct": false}]}, {"question_audio": "audio/osha_no_step.mp3", "prompt": {"en": "Select: ''Do not step on the top rung''", "zh": "請選擇：''不要踩最後一階''"}, "options": [{"id": "opt1", "text": {"en": "Top Step (Unsafe)", "zh": "頂階 (不安全)"}, "image": "assets/images/ladder_top_step.png", "correct": true}, {"id": "opt2", "text": {"en": "Middle Step", "zh": "中間階"}, "image": "assets/images/ladder_mid_step.png", "correct": false}]}]', 'kc_safety_imperatives', 2);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_const_1_3', 'u_const_1', '{"en": "Silica Dust Control", "zh": "矽粉塵控制"}', 'READING', '{"text": "\n            CONTROL DE POLVO DE SÍLICE:\n            El polvo de sílice cristalina respirable se crea al cortar, aserrar, esmerilar, taladrar o triturar piedra, roca, hormigón, ladrillo, bloque y mortero.\n            \n            Medidas de control:\n            1. Use agua para suprimir el polvo (Corte húmedo).\n            2. Use aspiradoras con filtros HEPA.\n            3. Use respiradores (máscaras) si los controles de ingeniería no son suficientes.\n            \n            ¡El polvo de sílice causa silicosis, una enfermedad pulmonar incurable!\n            ", "questions": [{"question": {"en": "What disease does silica dust cause?", "zh": "矽粉塵會導致什麼疾病？"}, "options": [{"en": "Flu", "zh": "流感"}, {"en": "Silicosis", "zh": "矽肺病"}, {"en": "Mild Asthma", "zh": "輕微氣喘"}, {"en": "Headache", "zh": "頭痛"}], "correctIndex": 1}, {"question": {"en": "What is an effective control method?", "zh": "哪種控制方法有效？"}, "options": [{"en": "Blowing with air", "zh": "吹氣"}, {"en": "Dry sweeping", "zh": "乾掃"}, {"en": "Using water (Wet cutting)", "zh": "用水 (濕切)"}, {"en": "Ignoring it", "zh": "忽略它"}], "correctIndex": 2}]}', 'kc_safety_imperatives', 3);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_legal_1_1', 'u_legal_1', '{"en": "Courtroom Roles", "zh": "法庭角色"}', 'FLASHCARD', '[{"front": "El Juez", "back": {"en": "The Judge", "zh": "法官"}, "example": {"en": "The judge dictates the sentence.", "zh": "El juez dicta la sentencia. (法官宣判。)"}}, {"front": "El Abogado", "back": {"en": "The Lawyer/Attorney", "zh": "律師"}, "example": {"en": "The lawyer defends the accused.", "zh": "El abogado defiende al acusado. (律師為被告辯護。)"}}, {"front": "El Acusado", "back": {"en": "The Defendant", "zh": "被告"}, "example": {"en": "The defendant pleads innocent.", "zh": "El acusado se declara inocente. (被告抗辯無罪。)"}}, {"front": "El Fiscal", "back": {"en": "The Prosecutor", "zh": "檢察官"}, "example": {"en": "The prosecutor presents charges.", "zh": "El fiscal presenta los cargos. (檢察官提出指控。)"}}]', 'kc_legal_vocabulary', 1);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_legal_1_2', 'u_legal_1', '{"en": "Common Procedures", "zh": "常見程序"}', 'QUIZ', '[{"question": {"en": "Translate: ''Arraignment''", "zh": "翻譯：''Arraignment'' (提審)"}, "options": [{"en": "Lectura de cargos", "zh": "Lectura de cargos (提審)"}, {"en": "Juicio", "zh": "Juicio (審判)"}, {"en": "Sentencia", "zh": "Sentencia (判決)"}, {"en": "Apelación", "zh": "Apelación (上訴)"}], "correctIndex": 0}, {"question": {"en": "What does ''Bajo juramento'' mean?", "zh": "''Bajo juramento'' 是什麼意思？"}, "options": [{"en": "Under arrest", "zh": "被逮捕"}, {"en": "Under oath", "zh": "宣誓下"}, {"en": "Under investigation", "zh": "被調查"}, {"en": "Underweight", "zh": "體重不足"}], "correctIndex": 1}]', 'kc_legal_vocabulary', 2);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_nomad_1_1', 'u_nomad_1', '{"en": "Visa Requirements", "zh": "簽證要求"}', 'READING', '{"text": "\n            Para solicitar el visado de nómada digital, usted debe demostrar:\n            1. Que trabaja de forma remota para empresas fuera de España.\n            2. Que tiene ingresos suficientes (al menos 200% del SMI).\n            3. Que no tiene antecedentes penales en los últimos 5 años.\n            ", "questions": [{"question": {"en": "What is the income requirement?", "zh": "收入要求是多少？"}, "options": ["100% of SMI", "200% of SMI", "No requirement", "5000 Euros"], "correctIndex": 1}, {"question": {"en": "Can you work for a Spanish company primarily?", "zh": "可以主要為西班牙公司工作嗎？"}, "options": [{"en": "Yes", "zh": "可以"}, {"en": "No, mostly outside Spain", "zh": "不行，主要需在西班牙境外"}, {"en": "Only Spanish companies", "zh": "只能為西班牙公司"}, {"en": "Whatever", "zh": "隨便"}], "correctIndex": 1}]}', 'kc_bureaucracy', 1);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_arch_1_1', 'u_arch_1', '{"en": "Romanesque & Gothic Art", "zh": "羅馬式與哥德式藝術"}', 'READING', '{"text": "\n            La arquitectura gótica española se caracteriza por la evolución desde el románico hacia estructuras más ligeras y luminosas.\n            \n            Elementos clave:\n            - Bóveda de crucería (Ribbed vault): Permite cubrir espacios más amplios.\n            - Arco apuntado (Pointed arch): Distribuye mejor el peso que el arco de medio punto románico.\n            - Arbotantes (Flying buttresses): Soportan el empuje de las bóvedas desde el exterior.\n            \n            En contraste, la arquitectura islámica en España (Al-Ándalus) introdujo el arco de herradura (Horseshoe arch) y el uso decorativo del alfiz.\n            ", "questions": [{"question": {"en": "What element allows covering wider spaces?", "zh": "什麼元素允許覆蓋更寬的空間？"}, "options": ["Arco de medio punto", "Bóveda de crucería", "Muro grueso", "Columna dórica"], "correctIndex": 1}, {"question": {"en": "What is an ''Arbotante''?", "zh": "''Arbotante'' (飛扶壁) 是什麼？"}, "options": [{"en": "A type of tree", "zh": "一種樹"}, {"en": "An exterior structural support", "zh": "外部結構支撐"}, {"en": "A circular window", "zh": "圓形窗戶"}, {"en": "A decorated door", "zh": "裝飾門"}], "correctIndex": 1}, {"question": {"en": "What arch is characteristic of Islamic architecture?", "zh": "伊斯蘭建築的特徵拱門是什麼？"}, "options": ["Arco apuntado", "Arco de herradura", "Arco plano", "Arco triunfal"], "correctIndex": 1}]}', 'kc_arch_terms', 1);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_tour_1_1', 'u_tour_1', '{"en": "Airport Survival", "zh": "機場生存"}', 'DIALOGUE', '[{"speaker": "Traveler", "es": "Perdón, ¿dónde está mi equipaje?", "translation": {"en": "Excuse me, where is my luggage?", "zh": "不好意思，我的行李在哪裡？"}, "audio_ref": "audio/airport_luggage.mp3"}, {"speaker": "Staff", "es": "En la cinta número 5.", "translation": {"en": "On belt number 5.", "zh": "在 5 號轉盤。"}, "audio_ref": "audio/airport_belt5.mp3"}]', 'kc_tourism_phrases', 1);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_tour_1_2', 'u_tour_1', '{"en": "Ordering Tacos", "zh": "點塔可餅"}', 'DIALOGUE', '[{"speaker": "Vendor", "es": "¿Con todo, joven?", "translation": {"en": "With everything, young man?", "zh": "帥哥，要加所有配料嗎？"}, "audio_ref": "audio/tacos_con_todo.mp3"}, {"speaker": "Traveler", "es": "Sin picante, por favor.", "translation": {"en": "No spicy, please.", "zh": "請不要加辣。"}, "audio_ref": "audio/tacos_no_spicy.mp3"}]', 'kc_tourism_phrases', 2);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_tour_2_1', 'u_tour_2', '{"en": "Airbnb Problem", "zh": "Airbnb 住宿問題"}', 'DIALOGUE', '[{"speaker": "Guest", "es": "Hola, el agua caliente no funciona.", "translation": {"en": "Hi, the hot water isn''t working.", "zh": "嗨，熱水器壞了。"}, "audio_ref": "audio/airbnb_water.mp3"}, {"speaker": "Host", "es": "Lo siento, enviaré a alguien ahora.", "translation": {"en": "Sorry, I''ll send someone now.", "zh": "抱歉，我馬上派人過去。"}, "audio_ref": "audio/airbnb_fix.mp3"}]', 'kc_tourism_phrases', 1);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_biz_1_1', 'u_biz_1', '{"en": "Usted vs Tú in Business", "zh": "商務中的您與你"}', 'QUIZ', '[{"question": {"en": "Which is appropriate for a new client?", "zh": "對新客戶應該用哪個稱呼？"}, "options": [{"en": "Tú (Informal)", "zh": "Tú (非正式)"}, {"en": "Usted (Formal)", "zh": "Usted (正式)"}, "Vos (Regional)", "Che (Regional)"], "correctIndex": 1}, {"question": {"en": "Translate: ''Estimado Señor''", "zh": "翻譯：''Estimado Señor''"}, "options": [{"en": "Dear Sir", "zh": "尊敬的先生"}, {"en": "Hey Mister", "zh": "嘿，先生"}, "Hi Man", "Good Morning"], "correctIndex": 0}]', 'kc_business_etiquette', 1);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_biz_2_1', 'u_biz_2', '{"en": "Supplier Negotiation", "zh": "供應商談判"}', 'DIALOGUE', '[{"speaker": "Manager", "es": "Necesitamos la entrega para el viernes.", "translation": {"en": "We need delivery by Friday.", "zh": "我們週五前需要收貨。"}, "audio_ref": "audio/biz_delivery.mp3"}, {"speaker": "Supplier", "es": "Es difícil, pero podemos intentarlo.", "translation": {"en": "It''s difficult, but we can try.", "zh": "有點難，但我們可以試試。"}, "audio_ref": "audio/biz_try.mp3"}]', 'kc_business_etiquette', 1);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_med_1_1', 'u_med_1', '{"en": "Pain Assessment", "zh": "疼痛評估"}', 'FLASHCARD', '[{"front": "Dolor agudo", "back": {"en": "Sharp pain", "zh": "劇痛/刺痛"}, "example": {"en": "I feel a sharp pain here.", "zh": "Siento un dolor agudo aquí. (我這裡感到劇痛。)"}}, {"front": "Dolor sordo", "back": {"en": "Dull ache", "zh": "隱痛/悶痛"}, "example": {"en": "It is a constant dull ache.", "zh": "Es un dolor sordo constante. (是持續的隱痛。)"}}, {"front": "Hinchazón", "back": {"en": "Swelling", "zh": "腫脹"}, "example": {"en": "There is a lot of swelling.", "zh": "Hay mucha hinchazón. (腫得很厲害。)"}}]', 'kc_medical_terms', 1);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_life_1_1', 'u_life_1', '{"en": "Opening a Bank Account", "zh": "銀行開戶"}', 'READING', '{"text": "\n            Para abrir una cuenta bancaria en España (Cuenta de No Residente), necesita:\n            1. Pasaporte válido.\n            2. Certificado de No Residente (de la Policía).\n            3. Prueba de dirección (empadronamiento o factura).\n            \n            Algunos bancos online (Neobancos) facilitan el proceso con video-identificación.\n            ", "questions": [{"question": {"en": "What certificate is needed?", "zh": "需要什麼證書？"}, "options": ["Birth Certificate", {"en": "Non-Resident Certificate", "zh": "非居民證明"}, "Driving License", "Marriage Certificate"], "correctIndex": 1}, {"question": {"en": "What alternative exists to traditional banks?", "zh": "傳統銀行有什麼替代方案？"}, "options": ["Cash only", {"en": "Neobanks (Online)", "zh": "網銀 (Neobancos)"}, "Crypto", "Barter"], "correctIndex": 1}]}', 'kc_bureaucracy', 1);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_gen_inter_1_1', 'u_gen_inter_1', '{"en": "The Subjunctive Mood", "zh": "虛擬語氣"}', 'DRILL', '[{"base": "Es importante que tú (comer) bien.", "substitution": "comas", "result": {"en": "Es importante que tú comas bien.", "zh": "重要的是你要吃好。"}}, {"base": "Dudo que él (venir).", "substitution": "venga", "result": {"en": "Dudo que él venga.", "zh": "我懷疑他會來。"}}, {"base": "Ojalá que nosotros (ganar).", "substitution": "ganemos", "result": {"en": "Ojalá que nosotros ganemos.", "zh": "希望我們能贏。"}}]', 'kc_subjunctive', 1);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_gen_inter_1_2', 'u_gen_inter_1', '{"en": "Accent Challenge: Argentina vs Spain", "zh": "口音挑戰：阿根廷 vs 西班牙"}', 'DIALOGUE', '[{"speaker": "Español", "es": "Aquí tienes las llaves del coche.", "translation": {"en": "Here are the car keys.", "zh": "車鑰匙給你。"}, "audio_ref": "audio/accent_spain_keys.mp3"}, {"speaker": "Argentino", "es": "¡Che! ¿Viste la lluvia ayer?", "translation": {"en": "Hey! Did you see the rain yesterday?", "zh": "欸！你看到昨天的雨了嗎？"}, "audio_ref": "audio/accent_arg_rain.mp3"}]', 'kc_accents', 2);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_gen_adv_1_1', 'u_gen_adv_1', '{"en": "Climate Change Report", "zh": "氣候變遷報告"}', 'READING', '{"text": "\n            EL CAMBIO CLIMÁTICO Y LA ECONOMÍA:\n            Según el último informe del IPCC, los fenómenos meteorológicos extremos están causando disrupciones significativas en la cadena de suministro global.\n            \n            Se estima que el PIB mundial podría contraerse un 18% para 2050 si no se toman medidas drásticas de mitigación. La transición hacia energías renovables ya no es una opción, sino un imperativo económico.\n            ", "questions": [{"question": {"en": "Which organization published the report?", "zh": "哪個組織發布了這份報告？"}, "options": ["ONU", "IPCC", "WHO", "FIFA"], "correctIndex": 1}, {"question": {"en": "What is the economic consequence mentioned?", "zh": "提到了什麼經濟後果？"}, "options": [{"en": "Growth", "zh": "成長 18%"}, {"en": "Contraction of 18%", "zh": "萎縮 18%"}, "Stability", "Zero Inflation"], "correctIndex": 1}]}', 'kc_current_events', 1);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_gen_adv_1_2', 'u_gen_adv_1', '{"en": "Debate: AI Ethics", "zh": "辯論：AI 倫理"}', 'ROLEPLAY', '{"system_prompt": "You are a philosophy professor debating the ethics of Artificial Intelligence. Challenge the user''s views on automation and privacy using complex sentence structures and C1 vocabulary.", "initial_message": "Considerando el avance exponencial de la IA, ¿cree usted que la privacidad individual es un sacrificio necesario para el progreso tecnológico?"}', 'kc_current_events', 2);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u1_1', 'u1', '{"en": "Basic Sentences", "zh": "基本句型"}', 'DIALOGUE', '[{"speaker": "A", "es": "Buenos días.", "translation": {"en": "Good morning.", "zh": "早安。"}, "audio_ref": "audio/l1_0_Buenos_días.mp3"}, {"speaker": "B", "es": "Buenos días, señor.", "translation": {"en": "Good morning, sir.", "zh": "早安，先生。"}, "audio_ref": "audio/l1_1_Buenos_días_señor.mp3"}, {"speaker": "A", "es": "¿Cómo está usted?", "translation": {"en": "How are you?", "zh": "您好嗎？"}, "audio_ref": "audio/l1_2_Cómo_está_usted.mp3"}]', 'kc_basic_greetings', 1);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u1_2', 'u1', '{"en": "Substitution Drill 1.1", "zh": "替換練習 1.1"}', 'DRILL', '[{"base": "Yo voy al banco.", "substitution": "al hotel", "result": {"en": "Yo voy al hotel.", "zh": "我去旅館。"}}, {"base": "Yo voy al hotel.", "substitution": "a la tienda", "result": {"en": "Yo voy a la tienda.", "zh": "我去商店。"}}, {"base": "Yo voy a la tienda.", "substitution": "al mercado", "result": {"en": "Yo voy al mercado.", "zh": "我去市場。"}}]', 'kc_subject_pronouns', 2);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u_gen_inter_1_1', 'u_gen_inter_1', '{"en": "Fútbol: La Pasión de Dos Mundos", "zh": "足球：兩個世界的熱情"}', 'DIALOGUE', '[{"speaker": "Mexicano", "es": "¡No manches! ¿Viste el golazo de ayer?", "translation": {"en": "No way! Did you see that great goal yesterday?", "zh": "不會吧！你看到昨天的世界波了嗎？"}, "audio_ref": "audio/mx_golazo.mp3"}, {"speaker": "Español", "es": "Sí, tío. Fue una pasada. El balón entró por la escuadra.", "translation": {"en": "Yeah, man. It was amazing. The ball went into the top corner.", "zh": "是啊，兄弟。太神了。球直掛死角。"}, "audio_ref": "audio/es_pasada.mp3"}, {"speaker": "Mexicano", "es": "Acá en México decimos que la colgó del ángulo.", "translation": {"en": "Here in Mexico we say he hung it from the angle.", "zh": "在我們墨西哥這叫掛死角。"}, "audio_ref": "audio/mx_angulo.mp3"}]', 'kc_accents', 1);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u0_1', 'u0', '{"en": "The Vowels (Las Vocales)", "zh": "母音 (Las Vocales)"}', 'FLASHCARD', '[{"front": "A", "back": {"en": "Sounds like ''a'' in ''father''. Never like ''cat''.", "zh": "發音像 ''爸爸'' (baba) 中的 ''a''。嘴巴張大。"}, "example": "Agua (Water)", "audio": "audio/intro_a.mp3"}, {"front": "E", "back": {"en": "Sounds like ''e'' in ''bed''.", "zh": "發音像 ''耶'' (ye) 中的 ''e''。嘴角向兩側咧開。"}, "example": "Elefante (Elephant)", "audio": "audio/intro_e.mp3"}, {"front": "I", "back": {"en": "Sounds like ''ee'' in ''see''.", "zh": "發音像 ''一'' (yi)。"}, "example": "Isla (Island)", "audio": "audio/intro_i.mp3"}, {"front": "O", "back": {"en": "Sounds like ''o'' in ''so''. Round your lips.", "zh": "發音像 ''喔'' (o)。嘴巴要圓。"}, "example": "Oso (Bear)", "audio": "audio/intro_o.mp3"}, {"front": "U", "back": {"en": "Sounds like ''oo'' in ''moon''.", "zh": "發音像 ''烏'' (wu)。嘴唇噘起。"}, "example": "Uvas (Grapes)", "audio": "audio/intro_u.mp3"}]', 'kc_pronunciation_vowels', 1);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u0_2', 'u0', '{"en": "Tricky Consonants", "zh": "易混淆子音"}', 'DRILL', '[{"base": {"en": "Pero (But)", "zh": "Pero (但是)"}, "substitution": {"en": "Perro (Dog)", "zh": "Perro (狗)"}, "result": {"en": "Trill the RR!", "zh": "RR 要用力彈舌！"}, "audio": "audio/intro_rr.mp3"}, {"base": {"en": "Niño (Boy)", "zh": "Niño (男孩)"}, "substitution": {"en": "Mañana (Tomorrow)", "zh": "Mañana (明天)"}, "result": {"en": "N with tilde is ''ny''", "zh": "Ñ 發音像 ''尼'' (ni)"}, "audio": "audio/intro_nye.mp3"}, {"base": {"en": "Hola (Hello)", "zh": "Hola (你好)"}, "substitution": {"en": "Hospital", "zh": "Hospital (醫院)"}, "result": {"en": "H is always silent", "zh": "H 永遠不發音"}, "audio": "audio/intro_h.mp3"}]', 'kc_pronunciation_consonants', 2);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u0_3', 'u0', '{"en": "Stress Rules Quiz", "zh": "重音規則測驗"}', 'QUIZ', '[{"question": {"en": "Where is the stress in ''Casa'' (ends in vowel)?", "zh": "''Casa'' (母音結尾) 的重音在哪裡？"}, "options": [{"en": "Last syllable (sa)", "zh": "最後一個音節 (sa)"}, {"en": "Second to last (Ca)", "zh": "倒數第二音節 (Ca)"}, {"en": "First syllable", "zh": "第一個音節"}, {"en": "Random", "zh": "隨機"}], "correctIndex": 1}, {"question": {"en": "Where is the stress in ''Hotel'' (ends in consonant L)?", "zh": "''Hotel'' (子音 L 結尾) 的重音在哪裡？"}, "options": [{"en": "Last syllable (tel)", "zh": "最後一個音節 (tel)"}, {"en": "First syllable (Ho)", "zh": "第一個音節 (Ho)"}, {"en": "Middle", "zh": "中間"}, {"en": "Silent", "zh": "不發音"}], "correctIndex": 0}]', 'kc_stress_rules', 3);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u2_1', 'u2', '{"en": "Dialogue: At the Office", "zh": "對話：在辦公室"}', 'DIALOGUE', '[{"speaker": "Sr. A", "es": "Buenas tardes, señor.", "translation": {"en": "Good afternoon, sir.", "zh": "午安，先生。"}, "audio_ref": "audio/l2_1_dialogue_0_Buenas_tardes_señor.mp3"}, {"speaker": "Sr. B", "es": "Buenas tardes. ¿Es usted el señor García?", "translation": {"en": "Good afternoon. Are you Mr. Garcia?", "zh": "午安。您是 Garcia 先生嗎？"}, "audio_ref": "audio/l2_1_dialogue_1_Buenas_tardes_Es_usted_el_seño.mp3"}, {"speaker": "Sr. A", "es": "Sí, señor. Y usted es el señor Molina, ¿verdad?", "translation": {"en": "Yes, sir. And you are Mr. Molina, right?", "zh": "是的。那您是 Molina 先生，對吧？"}, "audio_ref": "audio/l2_1_dialogue_2_Sí_señor_Y_usted_es_el_señor_M.mp3"}, {"speaker": "Sr. B", "es": "Sí, señor. Mucho gusto.", "translation": {"en": "Yes, sir. Nice to meet you.", "zh": "是的。很高興認識您。"}, "audio_ref": "audio/l2_1_dialogue_3_Sí_señor_Mucho_gusto.mp3"}]', 'kc_basic_greetings', 1);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u2_2', 'u2', '{"en": "Drill: Ser (To be)", "zh": "練習：Ser (是)"}', 'DRILL', '[{"base": "Yo soy cubano.", "substitution": "nosotros", "result": {"en": "Nosotros somos cubanos.", "zh": "我們是古巴人。"}}, {"base": "Nosotros somos cubanos.", "substitution": "ellos", "result": {"en": "Ellos son cubanos.", "zh": "他們是古巴人。"}}, {"base": "Ellos son cubanos.", "substitution": "él", "result": {"en": "Él es cubano.", "zh": "他是古巴人。"}}]', 'kc_subject_pronouns', 2);
INSERT OR REPLACE INTO lessons (id, unit_id, title, content_type, content_json, kc_id, order_index) VALUES ('l_u2_3', 'u2', '{"en": "Roleplay: Meeting a Stranger", "zh": "角色扮演：遇見陌生人"}', 'ROLEPLAY', '{"system_prompt": "You are a Spanish speaker meeting a stranger at a party. Be polite but casual. Use phrases like ''¿Cómo te llamas?'' and ''Mucho gusto''.", "initial_message": "¡Hola! Me llamo Carlos. ¿Y tú?"}', 'kc_basic_greetings', 3);