UPDATE lessons SET content_json = '[{"speaker": "Sr. Adams", "es": "Buenas tardes, señor.", "en": "Good afternoon, sir.", "audio_ref": "https://espanol-pro-content.xamide.workers.dev/l2_1_dialogue_0_Buenas_tardes_señor.mp3"}, {"speaker": "Sr. Molina", "es": "Buenas tardes. ¿Es usted el señor Adams?", "en": "Good afternoon. Are you Mr. Adams?", "audio_ref": "https://espanol-pro-content.xamide.workers.dev/l2_1_dialogue_1_Buenas_tardes_Es_usted_el_seño.mp3"}, {"speaker": "Sr. Adams", "es": "Sí, señor. Y usted es el señor Molina, ¿verdad?", "en": "Yes, sir. And you are Mr. Molina, right?", "audio_ref": "https://espanol-pro-content.xamide.workers.dev/l2_1_dialogue_2_Sí_señor_Y_usted_es_el_señor_M.mp3"}, {"speaker": "Sr. Molina", "es": "Sí, señor. Mucho gusto.", "en": "Yes, sir. Nice to meet you.", "audio_ref": "https://espanol-pro-content.xamide.workers.dev/l2_1_dialogue_3_Sí_señor_Mucho_gusto.mp3"}, {"speaker": "Sr. Adams", "es": "El gusto es mío.", "en": "The pleasure is mine.", "audio_ref": "https://espanol-pro-content.xamide.workers.dev/l2_1_dialogue_4_El_gusto_es_mío.mp3"}]' WHERE id = 'l2_1_dialogue';

